import { useEffect, useState } from "react";

const API = "http://localhost:5000";

function AIInsights() {
  const [insights, setInsights] = useState("");
  const [loading, setLoading] = useState(false);

  const token = localStorage.getItem("token");

  const fetchInsights = async () => {
    try {
      setLoading(true);

      const res = await fetch(`${API}/ai/insights`, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });

      const data = await res.json();
      setInsights(data.insights);

    } catch (err) {
      setInsights("Failed to load AI insights.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchInsights();
  }, []);

  return (
    <div style={{ padding: "15px", border: "1px solid #ccc", marginTop: "20px" }}>
      <h3>AI Budget Suggestions & Saving Tips</h3>

      {loading ? (
        <p>Analyzing your spending...</p>
      ) : (
        <p style={{ whiteSpace: "pre-line" }}>{insights}</p>
      )}

      <button onClick={fetchInsights} disabled={loading}>
        Refresh Insights
      </button>
    </div>
  );
}

export default AIInsights;
