import { useEffect, useState } from "react";

const API = "http://localhost:5000";

function AIInsights() {
  const [insights, setInsights] = useState("");
  const [loading, setLoading] = useState(false);
  const [aiAvailable, setAiAvailable] = useState(true); // ðŸ‘ˆ control visibility

  const token = localStorage.getItem("token");

  const fetchInsights = async () => {
    try {
      setLoading(true);

      const res = await fetch(`${API}/ai/insights`, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });

      const data = await res.json();

      // ðŸš« Hide quota / billing errors
      if (
        !res.ok ||
        data.message?.toLowerCase().includes("quota") ||
        data.message?.toLowerCase().includes("billing")
      ) {
        setAiAvailable(false);
        return;
      }

      setInsights(data.insights);
      setAiAvailable(true);

    } catch (err) {
      setAiAvailable(false);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchInsights();
  }, []);

  // ðŸ”• HIDE AI SECTION COMPLETELY IF NOT AVAILABLE
  if (!aiAvailable) {
    return null; // ðŸ‘ˆ hides AI insights section
  }

  return (
    <div style={{ padding: "15px", border: "1px solid #ccc", marginTop: "20px" }}>
      <h3>AI Budget Suggestions & Saving Tips</h3>

      {loading ? (
        <p>Analyzing your spending...</p>
      ) : (
        <p style={{ whiteSpace: "pre-line" }}>{insights}</p>
      )}

      <button onClick={fetchInsights}>Refresh Insights</button>
    </div>
  );
}

export default AIInsights;
